package Abc;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=21;
		{
		for( int i=1;i<=10;++i)
		{
			System.out.printf("%d* %d=%d \n" ,num,i,num*i);
		}
		
	}
	}
}



	
